// App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation, Navigate } from 'react-router-dom';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import HomePage from './components/HomePage';
import BestSellers from './pages/Bestsellers';
import NewSellers from './pages/Newsellers';
import LoginPage from './components/Login';
import Cart from './components/Cart';
import SignupPage from './components/Signup';
import './App.css';

const AppContent = ({ isAuthenticated, setIsAuthenticated }) => {
  const location = useLocation();
  const isAuthPage = location.pathname === '/login' || location.pathname === '/signup';

  return (
    <>
      {!isAuthPage && <Navbar />}
      <div className="content">
        <Routes>
          {/* Redirect to signup as the default */}
          <Route path="/" element={<Navigate to="/signup" />} />
          <Route path="/signup" element={<SignupPage onSignup={() => setIsAuthenticated(true)} />} />
          <Route path="/login" element={isAuthenticated ? <Navigate to="/" /> : <LoginPage onLogin={() => setIsAuthenticated(true)} />} />
          <Route path="/home" element={isAuthenticated ? <HomePage /> : <Navigate to="/login" />} />
          <Route path="/best-sellers" element={isAuthenticated ? <BestSellers /> : <Navigate to="/login" />} />
          <Route path="/new-sellers" element={isAuthenticated ? <NewSellers /> : <Navigate to="/login" />} />
          <Route path="/cart" element={isAuthenticated ? <Cart /> : <Navigate to="/login" />} />
        </Routes>
      </div>
      {!isAuthPage && <Footer />}
    </>
  );
};

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  return (
    <Router>
      <AppContent isAuthenticated={isAuthenticated} setIsAuthenticated={setIsAuthenticated} />
    </Router>
  );
};

export default App;
